import React, { useState, useEffect } from "react";
import PropTypes from "prop-types";
import { CSSTransition, TransitionGroup } from "react-transition-group";
import { email } from "../../config";
import styled from "styled-components";
import { theme, mixins, media, Section } from "../../styles/index";
const { colors, fontSizes, fonts, navDelay, loaderDelay } = theme;

const StyledContainer = styled(Section)`
  ${mixins.flexCenter};
  flex-direction: column;
  align-items: flex-start;
  min-height: 100vh;
  ${media.tablet`padding-top: 150px;`};
  div {
    width: 100%;
  }
`;
const StyledOverline = styled.h1`
  color: ${colors.blue};
  margin: 0 0 20px 3px;
  font-size: ${fontSizes.lg};
  font-family: ${fonts.SFMono};
  font-weight: normal;
  ${media.desktop`font-size: ${fontSizes.sm};`};
  ${media.tablet`font-size: ${fontSizes.smish};`};
`;
const StyledTitle = styled.h2`
  font-size: 80px;
  line-height: 1.1;
  margin: 0;
  ${media.desktop`font-size: 70px;`};
  ${media.tablet`font-size: 60px;`};
  ${media.phablet`font-size: 50px;`};
  ${media.phone`font-size: 40px;`};
`;
const StyledSubtitle = styled.h3`
  font-size: 80px;
  line-height: 1.1;
  color: ${colors.slate};
  ${media.desktop`font-size: 70px;`};
  ${media.tablet`font-size: 60px;`};
  ${media.phablet`font-size: 50px;`};
  ${media.phone`font-size: 40px;`};
`;
const StyledDescription = styled.div`
  margin-top: 25px;
  width: 50%;
  max-width: 500px;
  a {
    ${mixins.inlineLink};
  }
`;
const StyledEmailLink = styled.a`
  ${mixins.bigButton};
  margin-top: 50px;
`;

const Hero = (data) => {
  const [isMounted, setIsMounted] = useState(false);
  const frontmatter = data.frontmatter;
  //const data = JSON.parse(frontmatter);
  //console.log(frontmatter.frontmatter.id);
  //console.log(frontmatter);
  useEffect(() => {
    const timeout = setTimeout(() => setIsMounted(true), navDelay);
    return () => clearTimeout(timeout);
  }, []);

  const one = () => (
    <StyledOverline style={{ transitionDelay: "100ms" }}>
      {frontmatter.title}
    </StyledOverline>
  );

  const two = () => (
    <StyledTitle style={{ transitionDelay: "200ms" }}>
      {frontmatter.name}
    </StyledTitle>
  );

  const three = () => (
    <StyledSubtitle style={{ transitionDelay: "300ms" }}>
      {frontmatter.subtitle}
    </StyledSubtitle>
  );

  const four = () => (
    <StyledDescription
      style={{ transitionDelay: "400ms" }}
      dangerouslySetInnerHTML={{ __html: frontmatter.contentHtml }}
    />
  );

  const five = () => (
    <div style={{ transitionDelay: "500ms" }}>
      <StyledEmailLink href={`mailto:${email}`}>
        {frontmatter.buttonText}
      </StyledEmailLink>
    </div>
  );

  const items = [one, two, three, four, five];

  return (
    <StyledContainer id="hero">
      <TransitionGroup component={null}>
        {isMounted &&
          items.map((item, i) => (
            <CSSTransition key={i} classNames="fadeup" timeout={loaderDelay}>
              {item}
            </CSSTransition>
          ))}
      </TransitionGroup>
    </StyledContainer>
  );
};

Hero.propTypes = {
  data: PropTypes.object,
};

export default Hero;
